if MULTIPLAYER and CLIENT then return endoohh = {}oohh.Hooks = oohh.Hooks or {}function oohh.Hook(tag, callback)	oohh.Hooks[tag] = callbackendlocal function decode(str)	local args = sigh.Decode(str)	local id = args[1]	table.remove(args, 1)	return id, argsendlocal function encode(id, ...)	return sigh.Encode(id, ...)endfunction oohh.Send(id, ...)	oohh.StartMessageServer()		local str = encode(id, ...)	intermsg.Send("192.168.0.10", 27021, str)endfunction oohh.ReceiveMessage(str)	local id, args = decode(str)			if oohh.Hooks[id] then		oohh.Hooks[id](unpack(args))	endendevent.AddListener("IntermsgReceiveMessage", "oohh", function(ip, port, str)	oohh.ReceiveMessage(str)end)function oohh.StartMessageServer()	if not oohh.started then		intermsg.Host("192.168.0.10", 27011)		oohh.started = true	endend----oohh.Hook("echo", function(str)	print(str)end)oohh.Hook("rcon", function(line)	print("running command: " .. line)	console.RunString(line)end)oohh.Hook("lua", function(line)	print("running lua: " .. line)	easylua.RunLua(NULL, line, nil, true)end)event.AddListener("ConsoleOutput", "gmod", function(str)	oohh.Send("echo", str)end)oohh.Send("echo", "hello from oohh!")